import React, { Component } from 'react';
import Header from './Header/Header';
import Title from './SearchTitle/Title';
import SearchBody from './SearchBody/SearchBody';

class Search extends Component {
    constructor() {
        super();
        loc = 'Hi';
    }

    onLocationChange = (event) => {
        this.loc = event.target.value;
        alert('Location : '+ event.target.value);
    }

    render() { 
        return (  
            <div>
                <div>{this.props.location.search}</div>
                <Header />
                <Title text={`Exclusive restaurants at Hello !!`}/>
                <SearchBody onLocationChange={this.onLocationChange} queryString={this.props.location.search}/>
            </div>
        );
    }
}

export default Search;
